  const ref = useRef();
  const buttonDisable = (ref,idx) => {
    console.log("idx", idx);
    consol.log(ref.current);
    timeSelect[idx].props.disabled = 1;
    console.log(timeSelect[idx].props.disabled);
  };

  const handleNext = () => {
    const { next } = props;
    next();
  };

  useEffect(() => {
    const START_TIME = 9;
    for (let i = 0; i < START_TIME + 1; i++) {
      const cellTime = START_TIME * 1 + i;
      const cellStartTimeText = cellTime + ":00";
      const cellEndTimeText = cellTime + 1 + ":00";
      const inputCellText = cellStartTimeText + " ~ " + cellEndTimeText;

      console.log(i);
      setTimeList((timeList) => {
        return [...timeList, inputCellText];
      });
    }
  }, []);

  const timeSelect = timeList.map((item, idx) => {
    return (
      // <div className="button-timepick-padding" key={idx}>
      <Button
        key={idx}
        className="button-timepick"
        variant="primary"
        onClick={
          () => buttonDisable(idx)
          // props.addToCart(props.product);
          // onClickTime(idx);

          // handleNext;
        }
        disabled={0}
        ref={ref}
      >
        {item}
      </Button>
      // </div>
    );
  });
  console.log("랜더링 검사");

  return timeSelect;
}
